    <>
      <h3>Add Magazine</h3>
      <form onSubmit={submitHandler}>
        <label>Title</label>
        <input
          type="text"
          placeholder="Title"
          value={enteredTitle}
          onChange={(e) => {
            setTitle(e.target.value);
          }}
        />
        <label>ISBN</label>
        <input
          type="text"
          placeholder="ISBN"
          value={enteredIsbn}
          onChange={(e) => {
            setIsbn(e.target.value);
          }}
        />
        <label>Authors</label>
        <input
          type="text"
          placeholder="Authors"
          value={enteredAuthors}
          onChange={(e) => {
            setAuthors(e.target.value);
          }}
        />
        <label>PublishedAt</label>
        <input
          type="text"
          placeholder="dd.mm.yyyy"
          value={enteredDate}
          onChange={(e) => {
            setDate(e.target.value);
          }}
        />
        <button type="submit">Submit</button>
      </form>
    </>
  );
};

export default AddMagazine;
